INSERT INTO words (id, english, korean, pronunciation, part_of_speech, tip, category) VALUES (13053, 'เสร็จธุระแล้วรีบกลับบ้าน', '432. 볼일 (용건)을 마치고 집으로 서둘러 돌아 가다', '쎗↘   투↘라↗ 래애우↗ 리＾입 끌랍↘ 바＾안 끝내다, 마치다 일, 용건 이미 서두르다 돌아가다 집', NULL, NULL, 'thai-conversation');
