INSERT INTO words (id, english, korean, pronunciation, part_of_speech, tip, category) VALUES (13066, 'ชอบ นั่งริมหน้าต่าง', '445. 창가에 앉는 걸 좋아해.', '처＾업 낭＾ 림 나＾아 따앙↘ 좋아하다 앉다 가장자리 창문 『 ริม (림 : 가장자리) 의 다양한 모습들 』ริมฝีปาก (림 휘v이 빠악↘ : 입술) ริมทะเล (림 탈↗레애 : 바닷가)ริมแม่น้ำ (림 매＾애 나암↗ : 강가) ริมทาง (림 타앙 : 길가)', NULL, NULL, 'thai-conversation');
INSERT INTO words (id, english, korean, pronunciation, part_of_speech, tip, category) VALUES (13067, 'โดนเล่น งาน', '446. 꾸짖음을 당하다, 공격당하다, 압박당하다', '도온 레＾앤 응안 당하다 놀다, 몰두하다, 저지르다, 해를 가하다, 놀리다 일, 업무, 사정 꾸짖다, 공격하다, 압박하다, 행사에서 오락을 하다', NULL, NULL, 'thai-conversation');
